// Advanced Market Data Processing - Bloomberg Style
const allItems = $input.all();
const currentDate = new Date();
const dateStr = currentDate.toLocaleDateString('es-CO', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  timeZone: 'America/Bogota'
});

// Initialize comprehensive data structure
const marketData = {
  timestamp: currentDate.toISOString(),
  reportDate: dateStr,
  markets: {
    equities: {},
    bonds: {},
    commodities: {},
    forex: {}
  },
  economicIndicators: {},
  news: {
    marketMoving: [],
    regional: {
      colombia: [],
      latinamerica: [],
      unitedStates: [],
      europe: []
    },
    sectors: {
      coffee: [],
      sustainability: []
    },
    centralBanks: []
  },
  analysis: {
    topGainers: [],
    topLosers: [],
    volatility: null,
    sentiment: null
  }
};

// Helper function to calculate percentage change
function calcChange(current, previous) {
  if (!previous || previous === 0) return null;
  return ((current - previous) / previous * 100).toFixed(2);
}

// Helper function to format currency
function formatCurrency(value, decimals = 2) {
  return parseFloat(value).toFixed(decimals);
}

// Process each data source
allItems.forEach((item, index) => {
  const json = item.json;
  const nodeName = item.nodeName || '';

  // Process Equity Indices
  if (nodeName.includes('S&P 500')) {
    const quote = json['Global Quote'] || json;
    marketData.markets.equities.sp500 = {
      symbol: 'SPY',
      name: 'S&P 500',
      price: formatCurrency(quote['05. price'] || quote.price),
      change: formatCurrency(quote['09. change'] || quote.change),
      changePercent: quote['10. change percent'] || quote.changePercent,
      volume: quote['06. volume'] || quote.volume,
      previousClose: formatCurrency(quote['08. previous close'] || quote.previousClose)
    };
  }

  if (nodeName.includes('NASDAQ')) {
    const quote = json['Global Quote'] || json;
    marketData.markets.equities.nasdaq = {
      symbol: 'QQQ',
      name: 'NASDAQ-100',
      price: formatCurrency(quote['05. price'] || quote.price),
      change: formatCurrency(quote['09. change'] || quote.change),
      changePercent: quote['10. change percent'] || quote.changePercent,
      volume: quote['06. volume'] || quote.volume,
      previousClose: formatCurrency(quote['08. previous close'] || quote.previousClose)
    };
  }

  if (nodeName.includes('Dow Jones')) {
    const quote = json['Global Quote'] || json;
    marketData.markets.equities.dowJones = {
      symbol: 'DIA',
      name: 'Dow Jones Industrial Average',
      price: formatCurrency(quote['05. price'] || quote.price),
      change: formatCurrency(quote['09. change'] || quote.change),
      changePercent: quote['10. change percent'] || quote.changePercent,
      volume: quote['06. volume'] || quote.volume,
      previousClose: formatCurrency(quote['08. previous close'] || quote.previousClose)
    };
  }

  // Process Bond/Treasury Data
  if (nodeName.includes('10-Year Treasury')) {
    const observations = json.observations || [];
    if (observations.length > 0) {
      const latest = observations[0];
      marketData.markets.bonds.treasury10y = {
        name: '10-Year Treasury Yield',
        yield: formatCurrency(latest.value, 3),
        date: latest.date,
        change: observations.length > 1 ? calcChange(latest.value, observations[1].value) : null
      };
    }
  }

  if (nodeName.includes('Federal Funds Rate')) {
    const observations = json.observations || [];
    if (observations.length > 0) {
      const latest = observations[0];
      marketData.markets.bonds.fedFunds = {
        name: 'Federal Funds Rate',
        rate: formatCurrency(latest.value, 2),
        date: latest.date
      };
    }
  }

  // Process Commodities
  if (nodeName.includes('Gold')) {
    marketData.markets.commodities.gold = {
      name: 'Gold Spot',
      price: formatCurrency(json.price || json[0]?.price, 2),
      unit: 'USD/oz',
      change: json.change || null,
      changePercent: json.changePercent || null
    };
  }

  if (nodeName.includes('Silver')) {
    marketData.markets.commodities.silver = {
      name: 'Silver Spot',
      price: formatCurrency(json.price || json[0]?.price, 2),
      unit: 'USD/oz',
      change: json.change || null,
      changePercent: json.changePercent || null
    };
  }

  if (nodeName.includes('WTI Crude')) {
    marketData.markets.commodities.wti = {
      name: 'WTI Crude Oil',
      price: formatCurrency(json.price || json.data?.price, 2),
      unit: 'USD/barrel',
      change: json.change || json.data?.change || null,
      changePercent: json.changePercent || json.data?.changePercent || null
    };
  }

  if (nodeName.includes('Coffee Futures')) {
    const coffeeData = Array.isArray(json) ? json[0] : json;
    marketData.markets.commodities.coffeeArabica = {
      name: 'Coffee Futures (Arabica)',
      symbol: 'KC=F',
      price: formatCurrency(coffeeData.price, 2),
      unit: 'USD/lb',
      change: formatCurrency(coffeeData.change, 2),
      changePercent: formatCurrency(coffeeData.changesPercentage, 2),
      dayHigh: formatCurrency(coffeeData.dayHigh, 2),
      dayLow: formatCurrency(coffeeData.dayLow, 2),
      volume: coffeeData.volume
    };
  }

  // Process Forex Data
  if (nodeName.includes('Forex Rates')) {
    const rates = json.rates || {};
    marketData.markets.forex = {
      timestamp: json.date || currentDate.toISOString().split('T')[0],
      baseCurrency: 'USD',
      rates: {
        COP: {
          rate: formatCurrency(rates.COP, 2),
          name: 'Colombian Peso',
          inverse: formatCurrency(1 / rates.COP, 6)
        },
        EUR: {
          rate: formatCurrency(rates.EUR, 4),
          name: 'Euro',
          inverse: formatCurrency(1 / rates.EUR, 4)
        },
        MXN: {
          rate: formatCurrency(rates.MXN, 2),
          name: 'Mexican Peso'
        },
        BRL: {
          rate: formatCurrency(rates.BRL, 2),
          name: 'Brazilian Real'
        },
        GBP: {
          rate: formatCurrency(rates.GBP, 4),
          name: 'British Pound'
        },
        JPY: {
          rate: formatCurrency(rates.JPY, 2),
          name: 'Japanese Yen'
        },
        CHF: {
          rate: formatCurrency(rates.CHF, 4),
          name: 'Swiss Franc'
        }
      }
    };
  }

  if (nodeName.includes('TRM Banco Rep√∫blica')) {
    // Process official Colombian TRM data
    if (json.value || json.tasa || json.trm) {
      marketData.markets.forex.rates.COP.official = {
        trm: formatCurrency(json.value || json.tasa || json.trm, 2),
        source: 'Banco de la Rep√∫blica',
        date: json.fecha || json.date
      };
    }
  }

  // Process Economic Indicators
  if (nodeName.includes('CPI')) {
    const observations = json.observations || [];
    if (observations.length >= 2) {
      marketData.economicIndicators.inflation = {
        name: 'US Consumer Price Index',
        current: formatCurrency(observations[0].value, 1),
        previous: formatCurrency(observations[1].value, 1),
        change: calcChange(observations[0].value, observations[1].value),
        date: observations[0].date
      };
    }
  }

  if (nodeName.includes('Unemployment')) {
    const observations = json.observations || [];
    if (observations.length >= 2) {
      marketData.economicIndicators.unemployment = {
        name: 'US Unemployment Rate',
        current: formatCurrency(observations[0].value, 1),
        previous: formatCurrency(observations[1].value, 1),
        change: calcChange(observations[0].value, observations[1].value),
        date: observations[0].date,
        unit: '%'
      };
    }
  }

  if (nodeName.includes('VIX')) {
    const observations = json.observations || [];
    if (observations.length > 0) {
      const latest = observations[0];
      const previous = observations[1] || {};
      marketData.analysis.volatility = {
        name: 'CBOE Volatility Index (VIX)',
        current: formatCurrency(latest.value, 2),
        previous: formatCurrency(previous.value, 2),
        change: calcChange(latest.value, previous.value),
        date: latest.date,
        interpretation: parseFloat(latest.value) > 20 ? 'High Volatility' : parseFloat(latest.value) > 12 ? 'Normal' : 'Low Volatility'
      };
    }
  }

  // Process News Items
  if (json.title && json.link) {
    const newsItem = {
      title: json.title,
      summary: json.summary || json.description || json.contentSnippet || '',
      link: json.link || json.url,
      pubDate: json.pubDate || json.published || json.isoDate || '',
      source: nodeName,
      category: ''
    };

    // Categorize news
    if (nodeName.includes('Reuters') || nodeName.includes('Financial Times') || nodeName.includes("Barron's") || nodeName.includes('Economist')) {
      newsItem.category = 'Premium Financial News';
      marketData.news.marketMoving.push(newsItem);
    }

    if (nodeName.includes('Federal Reserve') || nodeName.includes('ECB')) {
      newsItem.category = 'Central Bank';
      marketData.news.centralBanks.push(newsItem);
    }

    if (nodeName.includes('Portafolio') || nodeName.includes('Dinero') || nodeName.includes('Rep√∫blica')) {
      newsItem.category = 'Colombia';
      marketData.news.regional.colombia.push(newsItem);
    }

    if (nodeName.includes('ICO') || nodeName.includes('Sprudge')) {
      newsItem.category = 'Coffee Industry';
      marketData.news.sectors.coffee.push(newsItem);
    }
  }
});

// Sort news by date (most recent first)
Object.keys(marketData.news.regional).forEach(region => {
  marketData.news.regional[region].sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate));
  marketData.news.regional[region] = marketData.news.regional[region].slice(0, 5);
});

marketData.news.marketMoving.sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate));
marketData.news.marketMoving = marketData.news.marketMoving.slice(0, 10);

marketData.news.centralBanks.sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate));
marketData.news.centralBanks = marketData.news.centralBanks.slice(0, 5);

marketData.news.sectors.coffee.sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate));
marketData.news.sectors.coffee = marketData.news.sectors.coffee.slice(0, 7);

// Calculate market statistics
marketData.statistics = {
  totalDataPoints: allItems.length,
  marketStatus: currentDate.getDay() >= 1 && currentDate.getDay() <= 5 ? 'Trading Day' : 'Weekend',
  dataQuality: {
    equitiesComplete: Object.keys(marketData.markets.equities).length >= 3,
    commoditiesComplete: Object.keys(marketData.markets.commodities).length >= 3,
    forexComplete: marketData.markets.forex.rates && Object.keys(marketData.markets.forex.rates).length >= 5,
    newsComplete: marketData.news.marketMoving.length >= 5
  }
};

return [marketData];

// Format Bloomberg-Style HTML Report
