const reportDate = new Date().toLocaleDateString('es-CO', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  timeZone: 'America/Bogota'
});

// Extract AI analysis content
let aiAnalysis = '';
if (analysisData.message && analysisData.message.content) {
  aiAnalysis = analysisData.message.content;
} else if (analysisData.content) {
  aiAnalysis = analysisData.content;
} else if (typeof analysisData === 'string') {
  aiAnalysis = analysisData;
}

// Enhanced markdown to HTML conversion
function markdownToHtml(text) {
  return text
    // Headers
    .replace(/### (.+)/g, '<h3 style="color: #1a1a1a; font-size: 18px; margin-top: 24px; margin-bottom: 12px; font-weight: 600; border-bottom: 2px solid #e8e8e8; padding-bottom: 6px;">$1</h3>')
    .replace(/## (.+)/g, '<h2 style="color: #000; font-size: 22px; margin-top: 32px; margin-bottom: 16px; font-weight: 700; border-bottom: 3px solid #0066cc; padding-bottom: 8px;">$1</h2>')
    .replace(/# (.+)/g, '<h1 style="color: #000; font-size: 26px; margin-top: 0; margin-bottom: 20px; font-weight: 700;">$1</h1>')
    // Bold and italic
    .replace(/\*\*(.+?)\*\*/g, '<strong style="font-weight: 600; color: #1a1a1a;">$1</strong>')
    .replace(/\*(.+?)\*/g, '<em>$1</em>')
    // Lists
    .replace(/^- (.+)$/gm, '<li style="margin: 6px 0; line-height: 1.6;">$1</li>')
    .replace(/(<li>.*<\/li>)/s, '<ul style="margin: 12px 0; padding-left: 24px;">$1</ul>')
    // Line breaks and paragraphs
    .replace(/\n\n/g, '</p><p style="margin: 12px 0; line-height: 1.7;">')
    .replace(/\n/g, '<br>');
}

