// Filtrar y procesar contenido
const items = $input.all();
const processedItems = [];
const seenTitles = new Set();

// Timestamp de 煤ltima ejecuci贸n (3 horas)
const lastRun = new Date(Date.now() - 3 * 60 * 60 * 1000);

items.forEach(item => {
  const post = item.json;

  // Verificar si es contenido nuevo
  const pubDate = new Date(post.pubDate || post.date || Date.now());
  if (pubDate < lastRun) return;

  // Deduplicaci贸n
  const titleHash = (post.title || '').toLowerCase().trim();
  if (seenTitles.has(titleHash)) return;
  seenTitles.add(titleHash);

  // Calcular relevancia
  let relevanceScore = 0;
  const keywords = ['innovaci贸n', 'tecnolog铆a', 'sostenibilidad', 'verde', 'impacto'];
  const content = `${post.title} ${post.description}`.toLowerCase();

  keywords.forEach(keyword => {
    if (content.includes(keyword)) relevanceScore += 10;
  });

  // Boost por recencia
  const hoursOld = (Date.now() - pubDate) / (1000 * 60 * 60);
  if (hoursOld < 1) relevanceScore += 20;
  else if (hoursOld < 6) relevanceScore += 10;

  // Limpiar descripci贸n
  const cleanDesc = (post.description || '')
    .replace(/<[^>]*>/g, '')
    .substring(0, 200)
    .trim();

  processedItems.push({
    title: post.title,
    link: post.link || post.url,
    description: cleanDesc,
    pubDate: pubDate.toISOString(),
    relevanceScore,
    id: post.link || `post-${Date.now()}`
  });
});

// Ordenar por relevancia y tomar top 3
processedItems.sort((a, b) => b.relevanceScore - a.relevanceScore);
const topPosts = processedItems.slice(0, 3);

console.log(`Procesados: ${items.length}, Filtrados: ${topPosts.length}`);

return topPosts.map(post => ({ json: post }));

// Generar idea de contenido
const topics = [
  'sostenibilidad empresarial',
  'innovaci贸n verde',
  'eficiencia energ茅tica',
  'econom铆a circular',
  'tecnolog铆a limpia'
];

const randomTopic = topics[Math.floor(Math.random() * topics.length)];

return [{
  json: {
    topic: randomTopic,
    needsGeneration: true,
    timestamp: new Date().toISOString()
  }
}];

// Construir post final
const input = $input.first().json;

let postText = '';

if (input.needsGeneration && input.choices) {
  // Contenido generado por IA
  postText = input.choices[0].message.content;
} else if (input.title) {
  // Contenido de RSS
  postText = ` ${input.title}\n\n${input.description}\n\n ${input.link}`;
}

// A帽adir hashtags
postText += '\n\n#Sostenibilidad #Innovaci贸n #GreenHub #Tecnolog铆aVerde';

return [{
  json: {
    text: postText,
    originalUrl: input.link || '',
    timestamp: new Date().toISOString()
  }
}];
