// Transformar datos de LinkedIn API a formato del portal
const items = [];

try {
  const response = $input.first().json;
  const elements = response.elements || [];

  for (const element of elements) {
    // Obtener detalles del post
    const shareUrn = element.organizationalEntity || '';
    const shareId = shareUrn.split(':').pop();

    items.push({
      id: Date.now() + items.length,
      title: `LinkedIn Update - ${shareId}`,
      content: element.commentary || element.specificContent?.shareCommentary?.text || 'Ver publicación en LinkedIn',
      description: element.commentary?.substring(0, 150) || '',
      date: new Date(element.created?.time || Date.now()).toISOString().split('T')[0],
      link: `https://www.linkedin.com/feed/update/${shareUrn}`,
      source: 'LinkedIn - Green Hub',
      isNew: true,
      category: 'LinkedIn',
      stats: {
        likes: element.totalShareStatistics?.likeCount || 0,
        comments: element.totalShareStatistics?.commentCount || 0,
        shares: element.totalShareStatistics?.shareCount || 0
      }
    });
  }
} catch (error) {
  console.error('Error parsing LinkedIn data:', error);
  throw new Error('Failed to parse LinkedIn API response: ' + error.message);
}

return items;
